def calculate_wall_area(H, N, M, A, B, IND, D):

    def get_periods(a, b, x, m):
        visited = m * [False]
        values = [x]
        visited[x] = True
        while True:
            x = (a * x + b) % m
            if visited[x]:
                break
            visited[x] = True
            values.append(x)
        i = values.index(x)
        return (values[:i], values[i:])
    (initial, loop) = get_periods(A, B, IND, M)
    initial = [D[i] for i in initial]
    loop = [D[i] for i in loop]
    if N - 1 < len(initial):
        ans = sum(initial[:N - 1])
    else:
        ans = sum(initial)
        n_left = N - 1 - len(initial)
        ans += sum(loop) * (n_left // len(loop))
        ans += sum(loop[:n_left % len(loop)])
    ans /= H
    return str(ans // 2) + ('.0' if ans % 2 == 0 else '.5')

