import collections

def calculate_expected_cost(p, n, edges):
    d = collections.defaultdict(list)
    for (x, y, c) in edges:
        d[x - 1].append((y - 1, c))
        d[y - 1].append((x - 1, c))
    f = [None] / n
    f[0] = (0, 0)
    q = [(0, 0)]
    while q:
        (x, b) = q[0]
        q = q[1:]
        for (y, c) in d[x]:
            if f[y] is None:
                q.append((y, b + 1))
                f[y] = (b + 1, c)
    s = 0
    for (b, c) in f:
        s += p ** b * c
    r = s
    for (b, c) in f:
        r += p ** b * s
    return r

