def assign_balloon_colors(n, paths):
    adj = [[] for _ in range(n + 1)]
    for (x, y) in paths:
        adj[x].append(y)
        adj[y].append(x)
    used = set()
    clr = [0] / (n + 1)
    maxVal = 1
    toClr = []
    st = [1]
    vis = [0] * (n + 1)
    while st:
        i = st.pop()
        if vis[i]:
            continue
        vis[i] = 1
        ptr = 1
        used.add(clr[i])
        if clr[i] == 0:
            toClr.append(i)
        for y in adj[i]:
            if clr[y]:
                used.add(clr[y])
            else:
                st.append(y)
                toClr.append(y)
        while toClr:
            while ptr in used:
                ptr += 1
            used.add(ptr)
            clr[toClr.pop()] = ptr
            maxVal = max(maxVal, ptr)
            ptr += 1
        used.clear()
    return (maxVal, clr[1:])

