def calculate_expected_soldiers_killed(n):
    A = [0, 1, 4]
    for i in range(100000):
        A.append(A[-1] + 4)
    A1 = A[:]
    A2 = A[:]
    for i in range(3, len(A)):
        A1[i] -= A1[i - 2]
        A2[i] += A2[i - 2]
    for i in range(1, len(A)):
        A2[i] *= 2
    for i in range(3, len(A)):
        A2[i] += A2[i - 2]
    if n == 1:
        return 1.0
    else:
        den = n * n
        ans = 0
        ans = A1[n] * (3 * n - 2)
        ans += A2[n - 2]
        prob = ans / float(den)
        return round(prob, 6)

