def query_stars_in_rectangular_regions(matrix, queries):
    """"""
    results = []
    for (px, py, qx, qy) in queries:
        total_stars = 0
        for i in range(px - 1, qx):
            for j in range(py - 1, qy):
                total_stars += matrix[i][j]
        results.append(total_stars)
    return results
