    p = list('ANOT')
    q = [0, 1, 2, 3]
    d = {p[i]: i for i in range(4)}
    n = len(a)
    cnt = [0] * 4
    x = [[0] * 4 for _ in range(1)]
    for i in range(n):
        j = d[a[i]]
        cnt[j] += 1
        for k in range(4):
            if j != k:
                x[j][k] += cnt[k]
    m = -1
    for u in itertools.permutations(q):
        ok = set()
        now = 0
        for i in u:
            for j in range(4):
                if j in ok:
                    now += x[j][i]
            ok.add(i)
        if m < now:
            m = now
            v = list(u)
    b = []
    for i in v:
        b.append(p[i] * cnt[d[p[i]]])
    return ''.join(b)

