    def dfs(G, start, path_exists):
        s = [start]
        visited = set([])
        if not start in path_exists:
            path_exists[start] = set([])
        while s:
            node = s.pop()
            if not node in visited:
                visited.add(node)
                path_exists[start].add(node)
                (a, b) = G[node]
                for (i, (c, d)) in enumerate(G):
                    if i != node:
                        if i in path_exists[start] or c < a < d or c < b < d:
                            s.append(i)
