from itertools import permutations as perm

def calculate_beautiful_string_cost(s, queries):
    n = len(s)
    pre = [[int(s[i] != pat[i % 3]) for i in range(n)] for pat in perm('abc')]
    for psum in pre:
        for i in range(1, n):
            psum[i] += psum[i - 1]
    results = []
    for (l, r) in queries:
        l += 1
        mn = 10 ** 9
        for psum in pre:
            res = psum[r - 1]
            if l > 0:
                res -= psum[l - 1]
            mn = min(mn, res)
        results.append(mn)
    return results

