    suniq = sorted(set(s))
    max_count = max((s.count(char) for char in suniq))
    chr_count = [0] * len(suniq)
    result = ''
    for _ in range(max_count):
        for i in range(len(suniq)):
            if chr_count[i] < s.count(suniq[i]):
                result += suniq[i]
                chr_count[i] += 1
        for i in range(len(suniq) - 1, -1, -1):
            if chr_count[i] < s.count(suniq[i]):
                result += suniq[i]
                chr_count[i] += -1
    return result
