    while wc < wl and bc < bl:
        weight = white[wc][0] - black[bc][0]
        edges.append([white[wc][1], black[bc][1], min(white[wc][0], black[bc][1])])
        if weight > 0 or (weight == 0 and wl - wc < bl - bc):
            white[wc][0] -= black[bc][0]
            bc += 1
        else:
            black[bc][0] -= white[wc][0]
            wc += 1
    return edges
