def count_substring_occurrences(s: str, t: str, queries: list) -> list:
    """"""
    n = len(s)
    m = len(t)
    pos = [0] * n
    for i in range(n):
        if i + m <= n and s[i:i + m] == t:
            pos[i] = 1
    sum_pos = [0] * (n + 1)
    for i in range(n):
        sum_pos[i + 1] = sum_pos[i] + pos[i]
    results = []
    for (l, r) in queries:
        r = r - m + 1
        l -= 1
        if l < r:
            results.append(sum_pos[r] - sum_pos[l])
        else:
            results.append(0)
    return results
