from collections import defaultdict

def min_assistants_to_cook_meals(n, k, arr):
    d = defaultdict(list)
    for i in range(n):
        d[arr[i]].append(i)
    ans = 1
    for key in d:
        val = d[key]
        val.append(1000000)
        prev = val[0]
        for index in val:
            if index > prev + k - 1:
                ans += 1
                prev = index
    return ans

