def validate_and_color_grid(t, grids):
    results = []
    for grid in grids:
        n = len(grid)
        m = len(grid[0])
        (r, w) = ([], [])
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 'R':
                    r.append(i + j)
                if grid[i][j] == 'W':
                    w.append(i + j)
        flag = 1
        if len(list(filter(lambda x: x % 2 == 0, r))) == 0 and len(list(filter(lambda x: x % 2 == 1, w))) == 0:
            flag = 1
        if len(list(filter(lambda x: x % 2 == 1, r))) == 0 and len(list(filter(lambda x: x % 2 == 0, w))) == 0:
            flag = 2
        if flag > 0:
            for i in range(n):
                new_row = []
                for j in range(m):
                    if grid[i][j] == '.':
                        if (i + j) % 2 == 0:
                            if flag == 1:
                                new_row.append('W')
                            else:
                                new_row.append('R')
                        elif flag == 1:
                            new_row.append('R')
                        else:
                            new_row.append('W')
                    else:
                        new_row.append(grid[i][j])
                grid[i] = ''.join(new_row)
            results.append((True, grid))
        else:
            results.append((False, None))
    return results

