def find_nth_prime_in_sequence(a: int, d: int, n: int) -> int:
    """"""
    MAXN = 10 ** 6
    sieve = [0] * 2 + [1] * MAXN
    n_idx = 2
    while n_idx * n_idx <= MAXN:
        if sieve[n_idx]:
            for i in range(2 * n_idx, MAXN + 1, n_idx):
                sieve[i] = 0
        n_idx += 1
    cnt = 0
    for i in range(a, MAXN + 1, d):
        if sieve[i]:
            cnt += 1
            if cnt == n:
                return i
    return -1
