def find_shortest_unordered_subsequence(sequence):
    n = len(sequence)
    if n <= 3:
        return (0, [])
    b = []
    for (i, x) in enumerate(sequence):
        if i == 0 or x != b[-1][0]:
            b.append([x, i])
    ind = []
    for i in range(len(b) - 2):
        if b[i][0] < b[i + 1][0] and b[i + 1][0] > b[i + 2][0] or (b[i][0] > b[i + 1][0] and b[i + 1][0] < b[i + 2][0]):
            ind.extend([b[i][1], b[i + 1][1], b[i + 2][1]])
            break
    if len(ind) > 0:
        return (3, [x + 1 for x in ind])
    else:
        return (0, [])

