def count_islands(grid):
    if not grid and (not grid[0]):
        return 0
    (m, n) = (len(grid), len(grid[0]))

    def dfs(row, col):
        if row < 0 or row >= m or col < 0 or (col >= n):
            return
        if grid[row][col] == 1:
            grid[row][col] = 0
            dfs(row + 1, col)
            dfs(row, col + 1)
            dfs(row - 1, col)
            dfs(row, col - 1)
            dfs(row + 1, col + 1)
            dfs(row - 1, col - 1)
            dfs(row - 1, col + 1)
            dfs(row + 1, col - 1)
    island_count = 0
    for i in range(m):
        for j in range(n):
            if grid[i][j] == 1:
                island_count += 1
                dfs(i, j)
    return island_count

