def calculate_steps_to_special_matrix(matrix, n):
    (star_row, star_col) = (-1, -1)
    for i in range(n):
        for j in range(n):
            if matrix[i][j] == '*':
                (star_row, star_col) = (i, j)
                break
        if star_row != -1:
            break
    middle = n / 2
    steps = abs(star_row - middle) + abs(star_col - middle)
    return steps

