def get_kth_permutation(n: int, k: int) -> str:
    nums = list('123456789')[:n]
    k += 1
    factor = 1
    for i in range(1, n):
        factor *= i
    res = []
    for i in reversed(range(n)):
        res.append(nums[k // factor])
        nums.remove(nums[k // factor])
        if i:
            k %= factor
            factor //= i
    return ''.join(res)

