def count_odd_sum_subsets(N, M, grid):

    def rankmod2(A):
        ret = 0
        i = 0
        j = 0
        while i < N and j < M:
            if A[i][j]:
                ret += 1
            else:
                for ii in range(i + 1, N):
                    if A[ii][j]:
                        (A[i], A[ii]) = (A[ii], A[i])
                        ret += 1
                        break
                else:
                    j += 1
                    continue
            for ii in range(i + 1, N):
                if A[ii][j]:
                    for jj in range(j, M):
                        A[ii][jj] = (A[ii][jj] + A[i][jj]) % 2
            i += 1
            j += 1
        return ret
    P = 998244354
    r = rankmod2(grid)
    return (pow(2, N + M - 1, P) - pow(2, N + M - r - 1, P)) % P

