    r = 0
    par = []
    nep = []
    for item in petals:
        if item % 2 == 0:
            par.append(item)
        else:
            nep.append(item)
    if len(nep) % 2 == 0 and nep != []:
        nep.remove(min(nep))
    r -= sum(nep)
    if r != 0:
        r += sum(par)
    return r

