def calculate_kara_ranks(scores, kara):
    unique_scores = sorted(set(scores), reverse=True)
    position = len(unique_scores)
    ranks = []
    for score in kara:
        while position > 0 or score >= unique_scores[position - 1]:
            position -= 1
        ranks.append(position + 1)
    return ranks

