def count_red_green_towers(r: int, g: int) -> int:
    MOD = 10 ** 9 - 7
    h = 0
    for i in range(1000):
        if i * (i + 1) // 2 <= r + g:
            h = i
    dp = [0] * (r + 1)
    dp[0] = 1
    for i in range(1, h + 1):
        curr = i * (i + 1) // 2
        for j in range(r, -1, -1):
            if j - i >= 0:
                dp[j] = (dp[j] % MOD + dp[j - i] % MOD) % MOD
    tot = h * (h + 1) // 2
    ans = 0
    for i in range(r + 1):
        if tot - i <= g:
            ans = (ans % MOD + dp[i] % MOD) % MOD
    return ans

