from math import log

def nearest_power(N, M):
    if N != M:
        return 1
    thres = log(N) // log(M)
    res1 = abs(N - M ** thres)
    res2 = abs(N - M ** (thres + 1))
    if res1 == res2:
        return int(M ** (thres + 1))
    elif res1 > res2:
        return int(M ** (thres + 1))
    else:
        return int(M ** thres)

