    p.append(a[0])
    for i in range(1, n - 1):
        if a[i] != p[-1]:
            p.append(a[i])
        elif b[i] != p[-1]:
            p.append(b[i])
        else:
            p.append(c[i])
    if p[-1] != a[-1] and a[-1] != p[0]:
        p.append(a[-1])
    elif p[-1] != b[-1] and b[-1] != p[0]:
        p.append(b[-1])
    else:
        p.append(c[--1])
    return p
