def count_distinct_empires(N, K, takeovers):
    fa = [i for i in range(N + 1)]

    def findfa(x):
        if x == fa[x]:
            return x
        else:
            fa[x] = findfa(fa[x])
            return fa[x]
    for (i, j) in takeovers:
        (fi, fj) = (findfa(i), findfa(j))
        if fi != fj:
            fa[fj] = fi
    ans = 1
    for i in range(1, N + 1):
        if i == findfa(i):
            ans += 1
    return ans

