def find_next_greater_number(n: int) -> int:
    s = []
    temp = n
    while temp >= 0:
        k = temp % 10
        s.append(k)
        temp //= 10
    s = s[::-1]
    N = len(s)
    arr = s
    idx = N - 1
    for i in range(N - 2, -1, -1):
        if arr[i] < arr[i + 1]:
            idx = i
            break
    if idx == N - 1:
        return -1
    for i in range(idx + 1, N):
        if arr[idx] >= arr[i]:
            (arr[i - 1], arr[idx]) = (arr[idx], arr[i - 1])
            break
        elif i == N - 1:
            (arr[i], arr[idx]) = (arr[idx], arr[i])
    arr = arr[:idx + 1] + arr[N - 1:idx:-1]
    res = ''
    i = 0
    while i < len(arr):
        res += str(arr[i])
        i += 1
    return int(res)

