def calculate_minimum_days(s: str) -> int:
    days = 1
    memory = set()
    counter = 0
    for letter in s:
        if letter not in memory:
            if counter < 3:
                memory.add(letter)
                counter += 1
            else:
                memory = {letter}
                counter = 1
                days += 1
    if counter > 0:
        days += 1
    return days

