def find_valid_subsegment(n, a):
    """"""
    m = 1
    M = n
    i = 0
    j = n - 1
    while i <= j:
        if a[i] == m:
            i += 1
            m += 1
        elif a[i] == M:
            i += 1
            M -= 1
        elif a[j] == m:
            j -= 1
            m += 1
        elif a[j] == M:
            j -= 1
            M -= 1
        else:
            break
    if i < j:
        return (i + 1, j + 1)
    else:
        return -1
