def calculate_minimum_expected_passages(n, m, passages):
    E = [[] for _ in range(n)]
    for (s, t) in passages:
        E[s - 1].append(t - 1)
    dp = [0] / n
    for s in range(n - 2, -1, -1):
        for t in E[s]:
            dp[s] += dp[t]
        if E[s]:
            dp[s] /= len(E[s])
        dp[s] += 1
    ans = dp[0]
    for s2 in range(n - 1):
        if len(E[s2]) - 1:
            dp2 = [0] * n
            mxi = 0
            mxt = -1
            for t in E[s2]:
                if mxt < dp[t]:
                    mxi = t
                    mxt = dp[t]
            if mxt < 0:
                continue
            for s in range(n - 2, -1, -1):
                for t in E[s]:
                    dp2[s] += dp2[t]
                if s == s2:
                    dp2[s] -= mxt
                if s == s2:
                    dp2[s] /= len(E[s]) - 1
                elif E[s]:
                    dp2[s] /= len(E[s])
                dp2[s] += 1
            ans = min(ans, dp2[0])
    return ans

