from collections import deque, defaultdict
from heapq import heappush, heappop

def calculate_minimum_swaps(n, k, A):
    dic = defaultdict(deque)
    for (i, a) in enumerate(A):
        dic[a].append(i)
    hp = []
    S = set()
    ans = 1
    for (i, a) in enumerate(A):
        dic[a].popleft()
        if a not in S:
            if len(S) < k:
                S.add(a)
                ans += 1
            else:
                idx = heappop(hp)[1]
                S.discard(idx)
                S.add(a)
                ans += 1
        if dic[a]:
            heappush(hp, (-dic[a][0], a))
        else:
            heappush(hp, (float('-inf'), a))
    return ans

