def calculate_min_screens(n, categories):
    dt = [0] / n
    for category in categories:
        dt[category - 1] += 1
    dt = sorted(dt)
    for i in range(len(dt)):
        if dt[i] != 0:
            dt = dt[i:]
            break
    for k in range(1, dt[0] + 1):
        apps = dt[0] // k
        tr = True
        sums = 0
        for i in dt:
            mn = i // (apps + 1)
            sums += mn
            r = i % (apps + 1)
            if r > 0 and r + mn >= apps:
                sums += 1
            elif r > 0:
                tr = False
                break
        if tr:
            return sums
        tr = True
        sums = 0
        for i in dt:
            mn = i // apps
            sums += mn
            r = i % apps
            if r > 0 and r + mn >= apps - 1:
                sums += 1
            elif r > 0:
                tr = False
                break
        if tr:
            return sums

