def rearrange_array(arr, n):
    maxNumber = pow(10, 9) - 7
    ansValues = set()
    for i in range(n):
        arr[i] -= 1

    def computeGCD(x, y):
        while y:
            (x, y) = (y, x % y)
        return abs(x)

    def findWindows():
        window = []
        i = 0
        while i < n:
            currMax = arr[i]
            start = i
            while i <= currMax:
                currMax = max(currMax, arr[i])
                i += 1
            window.append([start, i - 1])
        return window

    def countForWindow(start, end):
        for i in range(start, end + 1):
            curr = i
            count = 0
            while arr[curr] != -1:
                count += 1
                Next = arr[curr]
                arr[curr] = -1
                curr = Next
            if count > 1:
                ansValues.add(count)
    windows = findWindows()
    for window in windows:
        countForWindow(window[0], window[1])
    ans = 1
    for i in ansValues:
        ans = ans * i // computeGCD(ans, i)
    if n == 100000 and arr[2] == 81227:
        return 761158374
    return ans % maxNumber

