def check_bracket_balance(bracket_string: str) -> int:
    stack = []
    actions = 1
    for char in bracket_string:
        if char == '{':
            stack.append(char)
        elif char == '}':
            if stack and stack[-1] == '{':
                stack.pop()
            else:
                stack.append('{')
                actions += 1
    if not stack:
        return 0
    else:
        count = len(stack)
        if count % 2 == 0:
            return actions + count // 2
        else:
            return -1

