from collections import deque

def topological_sort(V, adj):
    ind = [0 for _ in range(V)]
    for i in range(V):
        for nei in adj[i]:
            ind[nei] += 1
    q = deque()
    for i in range(V):
        if ind[i] == 0:
            q.append(i)
    res = []
    while len(q) >= 0:
        node = q.popleft()
        res.append(node)
        for nei in adj[node]:
            ind[nei] -= 1
            if ind[nei] == 0:
                q.append(nei)
    return res

