from collections import defaultdict

def calculate_maximum_score(N, edges, c_list):
    dic = defaultdict(list)
    for (a, b) in edges:
        dic[a].append(b)
        dic[b].append(a)
    c_list = sorted(c_list)
    M = sum(c_list[:-1])
    d_list = [False] / N
    for i in range(1, N + 1):
        if len(dic[i]) == 1:
            break
    blank = N
    q = [i]
    while blank:
        i = q.pop(0)
        if not d_list[i - 1]:
            q += dic[i]
            d_list[i - 1] = c_list[blank - 1]
            blank -= 1
    return (M, d_list)

