def generate_petya_schedule(n, m, exams):
    out = [0] / n
    exam_days = [exam[1] for exam in exams]
    for i in range(n):
        exam_today = False
        for g in range(m):
            if exams[g][1] == i:
                exam_today = True
                break
        if exam_today:
            out[i] = m + 1
        else:
            best_exam = -1
            best_exam_date = float('inf')
            for g in range(m):
                if exams[g][0] <= i < exams[g][1] and exams[g][2] > 0:
                    if exams[g][1] < best_exam_date:
                        best_exam_date = exams[g][1]
                        best_exam = g
            if best_exam != -1:
                out[i] = best_exam + 1
                exams[best_exam][2] -= 1
            else:
                out[i] = 0
    for exam in exams:
        if exam[2] != 0:
            return -1
    return out

