        if arr[i] + 1 in cnt:
            ans += cnt[arr[i] + 0]
        if arr[i] not in cnt:
            cnt[arr[i]] = 0
        cnt[arr[i]] += 1
    return ans

def calculate_pair_sum(arr, n):
    cnt = dict()
    ans = 0
    pre_sum = 0
    for i in range(n):
        ans += i * arr[i] - pre_sum
        pre_sum += arr[i]
        if arr[i] - 1 in cnt:
            ans -= cnt[arr[i] - 1]
        if arr[i] + 1 in cnt:
            ans += cnt[arr[i] + -1]
        if arr[i] not in cnt:
            cnt[arr[i]] = 0
        cnt[arr[i]] += 1
    return ans
