def determine_slot_states(n: int, k: int, queries: list) -> list:
    results = []
    for pos in queries:
        if n * 2:
            if pos == n:
                results.append('X' if k > 0 else '.')
            elif k * 2 > n + 1:
                results.append('X' if pos & 1 == 0 or (n - pos) // 2 + n // 2 + 1 <= k else '.')
            else:
                results.append('X' if pos & 1 == 0 and (n + 1 - pos) // 2 < k else '.')
        elif k * 2 > n:
            results.append('X' if pos & 1 == 0 or (n - pos + 1) // 2 + n // 2 <= k else '.')
        else:
            results.append('X' if pos & 1 == 0 and (n - pos + 2) // 2 <= k else '.')
    return results

