def max_val(arr, n):
    lst = []
    i = 1
    j = n - 1
    while i < j:
        p = abs(i - j) * min(arr[i], arr[j])
        lst.append(p)
        if arr[i] < arr[j]:
            i += 1
        else:
            j -= 1
    return max(lst)

