from math import sqrt

def calculate_stolen_hay_blocks(n):
    """
    Calculate the minimally possible and maximally possible number of stolen hay blocks.

    Parameters:
    n (int): The number of hay blocks left in the barn after the theft.

    Returns:
    tuple: A tuple containing the minimum and maximum number of stolen hay blocks.
    """

    def f(x, y):
        return (x + 2) * (y + 2) + 2 * (x + y + 2) * n // (x * y)
    p = []
    for x in range(2, int(sqrt(n)) + 1):
        if n % x == 0:
            p.append(x)
    p -= [n // x for x in reversed(p)]
    p.append(n)
    u = v = f(1, 1)
    for m in p:
        for x in range(1, int(sqrt(m)) + 1):
            if m % x == 0:
                u = min(u, f(x, m // x))
    return (u, v)

