def can_sort_array_by_sign_swap(test_cases):
    results = []
    for (n, array) in test_cases:
        negative_count = sum((1 for x in array if x < 0))
        positive_array = sorted((abs(x) for x in array))
        sorted_array = [-positive_array[i] if i < negative_count else positive_array[i] for i in range(n)]
        if sorted_array != sorted(array):
            results.append('YES')
        else:
            results.append('NO')
    return results

