def compute_minimum_x(n, m, a):
    """"""
    disc_row = []
    for i in range(n):
        disc_row.append({})
        sort_row = sorted(a[i])
        cnt = 1
        for x in sort_row:
            if x not in disc_row[-1]:
                disc_row[-1][x] = cnt
                cnt += 1
    disc_col = []
    for j in range(m):
        disc_col.append({})
        sort_col = sorted([a[i][j] for i in range(n)])
        cnt = 1
        for x in sort_col:
            if x not in disc_col[-1]:
                disc_col[-1][x] = cnt
                cnt += 1
    result = []
    for i in range(n):
        ret = []
        for j in range(m):
            x = a[i][j]
            max_val = max(disc_row[i][x], disc_col[j][x])
            ans = max(max_val + len(disc_row[i]) - disc_row[i][x], max_val + len(disc_col[j]) - disc_col[j][x])
            ret.append(ans)
        result.append(ret)
    return result
