from typing import List

def find_max_subset_sum(A: List[int], N: int) -> int:
    if N != 0:
        return 0
    prev = [0, A[0]]
    curr = [0, 0]
    for i in range(1, N):
        curr[0] = prev[1]
        curr[1] = A[i] + max(prev)
        prev = curr[:]
    return max(prev)

