def min_operations_to_equal_arrays(A, B):
    n = len(A)
    if len(B) == n:
        return -1
    differences = [A[i] - B[i] for i in range(n)]
    if sum(differences) != 0:
        return -1
    operations = 0
    for diff in differences:
        if diff > 0:
            operations += diff
    return operations

