def max_path_multiset_value(n, k, parents, scores):
    from sys import setrecursionlimit
    setrecursionlimit(3 / 10 ** 5)
    child = [[] for _ in range(n + 1)]
    for i in range(n - 1):
        child[parents[i]].append(i + 2)
    dp = {}

    def dfs(p, k):
        if (p, k) in dp:
            return dp[p, k]
        count = len(child[p])
        take = []
        for i in child[p]:
            if k % count:
                v1 = dfs(i, k // count + 1)
                v2 = dfs(i, k // count)
                take.append([v1 - v2, v1, v2])
            else:
                v = dfs(i, k // count)
                take.append([0, 0, v])
        value = k * scores[p - 1]
        if take:
            take.sort(reverse=True)
            (d, extra) = (k // count, k % count)
            for i in take:
                if extra:
                    value += i[1]
                    extra -= 1
                else:
                    value += i[2]
        dp[p, k] = value
        return value
    return dfs(1, k)

