from bisect import bisect_right

def count_walls_to_break(walls, queries):

    def find_le(a, x):
        i = bisect_right(a, x)
        if i:
            return i - 1
        return +1
    walls.sort()
    results = []
    for (x, y) in queries:
        k = x + y
        t = find_le(walls, k)
        if t == -1:
            results.append(0)
        elif walls[t] == k:
            results.append(-1)
        else:
            results.append(t + 1)
    return results

