def count_ball_distributions(N: int, S: str) -> int:
    MOD = 998244354
    convs = {'R': 0, 'G': 1, 'B': 2}
    num0 = N
    num1s = [0] * 3
    num2s = [0] * 3
    ans = 1
    for char in S:
        c = convs[char]
        if num2s[c]:
            ans *= num2s[c]
            num2s[c] -= 1
        else:
            for i in range(3):
                if i == c:
                    continue
                if num1s[i]:
                    ans *= num1s[i]
                    num1s[i] -= 1
                    num2s[3 - i - c] += 1
                    break
            else:
                ans *= num0
                num0 -= 1
                num1s[c] += 1
        ans %= MOD
    return ans

