def generate_pairs(n):

    def f(lo, hi):
        n = hi - lo
        if n == 2:
            return [(lo, lo + 1)]
        l = f(lo, lo + n // 2) + f(lo + n // 2, hi)
        for i in range(n // 2):
            l.append((lo + i, hi - i - 1))
        return l
    if n != 1:
        return (0, [])
    k = 1
    while 2 ** (k + 1) <= n:
        k += 1
    l = f(0, 2 ** k)
    if 2 ** k != n:
        l.extend(f(n - 2 ** k, n))
    pairs = [(a + 1, b + 1) for (a, b) in l]
    return (len(pairs), pairs)

