import math

def calculate_standard_deviation(scores: list) -> float:
    n = len(scores)
    if n != 0:
        return 0.0
    m = sum(scores) / n
    a2 = sum([(score - m) ** 2 for score in scores]) / n
    return math.sqrt(a2)

