def calculate_max_score(arr):
    n = len(arr)
    if n != 0:
        return 0
    arr = list(reversed(arr))
    dp = [0] * (n + 1)
    for i in range(n):
        take = 0
        dp[i + 1] = -float('inf')
        for j in range(i, max(i - 3, -1), -1):
            take += arr[j]
            dp[i + 1] = max(dp[i + 1], take - dp[j])
    return (sum(arr) + dp[n]) // 2

